mapper.select:
select *
from user_teacher
where ${basis} = #{id}
  and password = #{password}

mapper.update:
update user_teacher
set id=#{id},
    name=#{name},
    phone=#{phone},
    email=#{email},
    password=#{password},
    department=#{department}
where ${signBasis} = #{signId}
  and password = #{signPassword}

mapper.delete:
delete
from user_student
where ${basis} = #{id}
  and password = #{password}
